mapper.insert:
     	merge into daily
     		using dual on(
     					daily.pid = #{pid} and
     					daily.cid = #{cid} and
     					daily.dt = #{dt}
     					)
     		when matched then update set
     				cnt = #{cnt}
     		when not matched then insert
     				(pid, cid, dt, cnt)
	     				values (#{pid}, #{cid}, #{dt}, #{cnt})

mapper.update:
update batch set st = #{st},
				  ed_dt = sysdate
where bid = #{bid}
