mapper.select:
select /*+ INDEX(users PK_users) */ users.*, to_char(birth, 'yyyy-MM-dd') birthStr
from users
where userId = #{userId}

mapper.update:
update users
	set name = #{name}, alias = #{alias}, addr1 = #{addr1},
	  	 addr2 = #{addr2}, zipcd = #{zipcd}, birth = #{birth}

	    	, path = #{path}, filename = #{filename} 

where userid = #{userId}
